@@ -64,7 +64,12 @@ if ( ! filter_var($url, FILTER_VALIDATE_URL) OR $link !== ee('Security/XSS')->cl
 // Make sure all requests to iframe this page are denied
 header('X-Frame-Options: SAMEORIGIN');

